#!/usr/bin/env node
/**
 * Scrape South Shore Stars website for program information
 */

const https = require('https');
const http = require('http');
const { URL } = require('url');
const fs = require('fs');

const visited = new Set();
const toVisit = new Set(['https://southshorestars.org']);
const allContent = {};
const maxPages = 50;

function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

function fetchPage(url) {
  return new Promise((resolve, reject) => {
    const urlObj = new URL(url);
    const protocol = urlObj.protocol === 'https:' ? https : http;

    const options = {
      headers: {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
      }
    };

    protocol.get(url, options, (res) => {
      if (res.statusCode !== 200) {
        reject(new Error(`Status: ${res.statusCode}`));
        return;
      }

      let data = '';
      res.on('data', chunk => data += chunk);
      res.on('end', () => resolve(data));
    }).on('error', reject);
  });
}

function extractLinks(html, baseUrl) {
  const links = new Set();
  const linkRegex = /<a[^>]+href=["']([^"']+)["']/gi;
  let match;

  while ((match = linkRegex.exec(html)) !== null) {
    try {
      const href = match[1];
      const fullUrl = new URL(href, baseUrl);

      if (fullUrl.hostname.includes('southshorestars.org')) {
        const cleanUrl = `${fullUrl.protocol}//${fullUrl.host}${fullUrl.pathname}`;
        links.add(cleanUrl);
      }
    } catch (e) {
      // Invalid URL, skip
    }
  }

  return links;
}

function extractTextContent(html) {
  // Remove script and style tags
  let text = html.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '');
  text = text.replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, '');

  // Remove HTML tags
  text = text.replace(/<[^>]+>/g, ' ');

  // Decode HTML entities
  text = text.replace(/&nbsp;/g, ' ');
  text = text.replace(/&amp;/g, '&');
  text = text.replace(/&lt;/g, '<');
  text = text.replace(/&gt;/g, '>');
  text = text.replace(/&quot;/g, '"');
  text = text.replace(/&#39;/g, "'");

  // Clean up whitespace
  text = text.replace(/\s+/g, ' ').trim();

  return text;
}

function extractTitle(html) {
  const match = html.match(/<title[^>]*>([^<]+)<\/title>/i);
  return match ? match[1].trim() : 'No title';
}

async function crawl() {
  console.log('Starting crawl of https://southshorestars.org...\n');

  while (toVisit.size > 0 && visited.size < maxPages) {
    const url = Array.from(toVisit)[0];
    toVisit.delete(url);

    if (visited.has(url)) continue;

    console.log(`Crawling: ${url}`);
    visited.add(url);

    try {
      const html = await fetchPage(url);
      const title = extractTitle(html);
      const content = extractTextContent(html);

      allContent[url] = { title, content };

      // Extract and add new links
      const links = extractLinks(html, url);
      links.forEach(link => {
        if (!visited.has(link)) {
          toVisit.add(link);
        }
      });

      // Be nice to the server
      await sleep(1000);

    } catch (error) {
      console.error(`Error crawling ${url}: ${error.message}`);
    }
  }

  console.log(`\nCrawled ${visited.size} pages`);

  // Save results
  fs.writeFileSync(
    '/job/tmp/scraped_content.json',
    JSON.stringify(allContent, null, 2),
    'utf-8'
  );

  console.log('Content saved to /job/tmp/scraped_content.json');
}

crawl().catch(console.error);

#!/usr/bin/env node
/**
 * Analyze scraped content and generate report
 */

const fs = require('fs');

// Load scraped content
const content = JSON.parse(fs.readFileSync('/job/tmp/scraped_content.json', 'utf-8'));

// Helper function to find pages containing keywords
function findPagesWithKeywords(keywords) {
  const results = [];
  for (const [url, page] of Object.entries(content)) {
    const text = page.content.toLowerCase();
    const title = page.title.toLowerCase();

    if (keywords.some(kw => text.includes(kw.toLowerCase()) || title.includes(kw.toLowerCase()))) {
      results.push({ url, title: page.title, content: page.content });
    }
  }
  return results;
}

// Helper to extract relevant sections
function extractSection(text, keywords, contextWords = 300) {
  const lowerText = text.toLowerCase();
  const sections = [];

  for (const keyword of keywords) {
    const index = lowerText.indexOf(keyword.toLowerCase());
    if (index !== -1) {
      const start = Math.max(0, index - contextWords);
      const end = Math.min(text.length, index + keyword.length + contextWords);
      const section = text.substring(start, end);
      sections.push(section);
    }
  }

  return sections;
}

console.log('Analyzing content for South Shore Stars report...\n');

// Find relevant pages
const schoolPages = findPagesWithKeywords(['stars school', 'learning center', 'elementary', 'dyslexia']);
const admissionPages = findPagesWithKeywords(['admission', 'enroll', 'apply']);
const tuitionPages = findPagesWithKeywords(['tuition', 'cost', 'fee', 'price']);
const staffPages = findPagesWithKeywords(['staff', 'teacher', 'therapist', 'qualifications']);
const therapyPages = findPagesWithKeywords(['therapy', 'therapist', 'speech', 'occupational', 'counseling']);
const contactPages = findPagesWithKeywords(['contact', 'phone', 'email', 'address']);

// Generate report
let report = `# South Shore Stars School & Learning Center - Comprehensive Report

**Website:** https://southshorestars.org  
**Report Generated:** ${new Date().toISOString()}  
**Total Pages Analyzed:** ${Object.keys(content).length}

---

## Executive Summary

South Shore Stars School & Learning Center is a specialized educational facility serving students with dyslexia and other learning differences. This report compiles information about their elementary dyslexia program, admissions process, costs, staff qualifications, and available services.

---

`;

// Generate each section
const sections = [
  {
    title: 'Elementary Dyslexia Program',
    pages: schoolPages,
    keywords: ['dyslexia', 'elementary', 'program', 'curriculum', 'orton', 'structured literacy']
  },
  {
    title: 'Admissions Process',
    pages: admissionPages,
    keywords: ['admission', 'enroll', 'application', 'process', 'requirements']
  },
  {
    title: 'Tuition and Costs',
    pages: tuitionPages,
    keywords: ['tuition', 'cost', 'fee', 'price', 'financial', 'scholarship']
  },
  {
    title: 'Staff Qualifications',
    pages: staffPages,
    keywords: ['staff', 'teacher', 'qualified', 'certification', 'credential', 'experience']
  },
  {
    title: 'Student-to-Teacher Ratios',
    pages: staffPages,
    keywords: ['ratio', 'class size', 'student teacher', 'small group']
  },
  {
    title: 'Therapy Services',
    pages: therapyPages,
    keywords: ['therapy', 'speech', 'occupational', 'counseling', 'support services']
  },
  {
    title: 'Contact Information',
    pages: contactPages,
    keywords: ['contact', 'phone', 'email', 'address', 'location']
  }
];

for (const section of sections) {
  report += `## ${section.title}\n\n`;

  if (section.pages.length === 0) {
    report += `*No specific information found for this topic.*\n\n`;
    report += `**Relevant Pages:** None found\n\n---\n\n`;
    continue;
  }

  // Deduplicate pages
  const uniquePages = [...new Map(section.pages.map(p => [p.url, p])).values()];

  for (const page of uniquePages.slice(0, 5)) { // Limit to top 5 pages per section
    report += `### Source: ${page.title}\n`;
    report += `**URL:** ${page.url}\n\n`;

    // Extract relevant sections
    const sections = extractSection(page.content, section.keywords, 400);

    if (sections.length > 0) {
      for (const sec of sections.slice(0, 3)) { // Limit to 3 excerpts per page
        const cleaned = sec.replace(/\s+/g, ' ').trim();
        if (cleaned.length > 50) { // Only include substantial excerpts
          report += `> ${cleaned}\n\n`;
        }
      }
    } else {
      // If no specific sections found, show beginning of content
      const preview = page.content.substring(0, 500).replace(/\s+/g, ' ').trim();
      report += `> ${preview}...\n\n`;
    }
  }

  report += `**Total Relevant Pages:** ${uniquePages.length}\n\n`;
  report += `---\n\n`;
}

// Add all pages list
report += `## All Pages Crawled\n\n`;
report += `Total pages: ${Object.keys(content).length}\n\n`;

const pagesByCategory = {};
for (const [url, page] of Object.entries(content)) {
  const pathParts = new URL(url).pathname.split('/').filter(Boolean);
  const category = pathParts[0] || 'home';

  if (!pagesByCategory[category]) {
    pagesByCategory[category] = [];
  }
  pagesByCategory[category].push({ url, title: page.title });
}

for (const [category, pages] of Object.entries(pagesByCategory).sort()) {
  report += `### ${category.charAt(0).toUpperCase() + category.slice(1)}\n\n`;
  for (const page of pages.sort((a, b) => a.url.localeCompare(b.url))) {
    report += `- [${page.title}](${page.url})\n`;
  }
  report += `\n`;
}

// Save report
fs.writeFileSync('/job/tmp/report_draft.md', report, 'utf-8');
console.log('Report draft created at /job/tmp/report_draft.md');
console.log(`Report length: ${report.length} characters`);
