        // Get page content
        console.log("\n2. Analyzing page structure...");

        // Get visible text
        const bodyText = await page.locator("body").innerText();
        console.log("\n3. Page text content:");
        console.log(bodyText.substring(0, 1000));

        // Look for buttons and links
        const buttons = await page.locator("button").all();
        console.log(`\n4. Found ${buttons.length} buttons on page`);

        const links = await page.locator("a").all();
        console.log(`   Found ${links.length} links on page`);

        // Look for transcription-related elements
        console.log("\n5. Looking for transcription features...");

        const keywords = ["transcribe", "audio", "youtube", "video", "upload", "URL"];
        for (const keyword of keywords) {
            const elements = await page.getByText(keyword, { exact: false }).all();
            if (elements.length > 0) {
                console.log(`   Found ${elements.length} elements with '${keyword}'`);
                // Get the text of the first few elements
                for (let i = 0; i < Math.min(3, elements.length); i++) {
                    const text = await elements[i].innerText().catch(() => "");
                    if (text) console.log(`      - "${text.substring(0, 100)}"`);
                }
            }
        }

        // Save page HTML for analysis
        const content = await page.content();
        const fs = require('fs');
        fs.writeFileSync("/job/tmp/page_content.html", content);
        console.log("\n6. Full page HTML saved to page_content.html");

        await browser.close();
        process.exit(0);

    } catch (error) {
        console.error(`Error: ${error.message}`);
        if (browser) {
            const page = (await browser.contexts()[0]?.pages())?.[0];
            if (page) await page.screenshot({ path: "/job/tmp/error_screenshot.png" });
            await browser.close();
        }
        process.exit(1);
    }
}

main();
