const { chromium } = require('playwright');

async function tryUrl(page, url, name) {
    try {
        console.log(`\nTrying ${name}: ${url}`);
        const response = await page.goto(url, { 
            waitUntil: "networkidle", 
            timeout: 30000 
        });
        const status = response.status();
        console.log(`  Status: ${status}`);

        if (status === 200) {
            await page.waitForTimeout(2000);
            const bodyText = await page.locator("body").innerText();
            console.log(`  Content preview: ${bodyText.substring(0, 200)}...`);

            await page.screenshot({ path: `/job/tmp/screenshot_${name.replace(/[^a-z0-9]/gi, '_')}.png` });
            console.log(`  Screenshot saved`);

            // Save HTML
            const content = await page.content();
            const fs = require('fs');
            fs.writeFileSync(`/job/tmp/content_${name.replace(/[^a-z0-9]/gi, '_')}.html`, content);

            return true;
        }
        return false;
    } catch (error) {
        console.log(`  Error: ${error.message}`);
        return false;
    }
}

async function main() {
    console.log("Exploring media.wat-factory.cloud for NCA Toolkit...");

    let browser;
    try {
        browser = await chromium.connectOverCDP("http://localhost:9222");
        const context = browser.contexts()[0] || await browser.newContext();
        const page = await context.newPage();

        // Try various paths
        const paths = [
            { name: "root", url: "https://media.wat-factory.cloud" },
            { name: "nca", url: "https://media.wat-factory.cloud/nca" },
            { name: "nca-toolkit", url: "https://media.wat-factory.cloud/nca-toolkit" },
            { name: "toolkit", url: "https://media.wat-factory.cloud/toolkit" },
            { name: "transcribe", url: "https://media.wat-factory.cloud/transcribe" },
            { name: "audio", url: "https://media.wat-factory.cloud/audio" },
            { name: "tools", url: "https://media.wat-factory.cloud/tools" },
            { name: "api", url: "https://media.wat-factory.cloud/api" },
        ];

        let foundUrl = null;
        for (const path of paths) {
            if (await tryUrl(page, path.url, path.name)) {
                foundUrl = path.url;
                console.log(`\nâœ“ Found working URL: ${foundUrl}`);
                break;
            }
        }

        if (!foundUrl) {
            console.log("\nNone of the common paths worked. Let me try the root domain without path...");
            // Try just the domain
            await tryUrl(page, "https://wat-factory.cloud", "wat-factory-root");
        }

        await browser.close();
        process.exit(0);

    } catch (error) {
        console.error(`Error: ${error.message}`);
        if (browser) await browser.close();
        process.exit(1);
    }
}

main();
