const { chromium } = require('playwright');

async function checkUrl(url) {
    try {
        const response = await fetch(url, { 
            method: 'HEAD',
            redirect: 'follow'
        });
        return { url, status: response.status, ok: response.ok };
    } catch (error) {
        return { url, error: error.message };
    }
}

async function main() {
    console.log("Checking various subdomains and paths...\n");

    const urls = [
        "https://nca.wat-factory.cloud",
        "https://toolkit.wat-factory.cloud",
        "https://app.wat-factory.cloud",
        "https://api.wat-factory.cloud",
        "https://media.wat-factory.cloud/",
        "http://media.wat-factory.cloud/",
        "https://www.wat-factory.cloud",
    ];

    for (const url of urls) {
        const result = await checkUrl(url);
        if (result.error) {
            console.log(`${url} - Error: ${result.error}`);
        } else {
            console.log(`${url} - Status: ${result.status} ${result.ok ? 'âœ“' : 'âœ—'}`);
        }
    }

    // Also try to resolve DNS
    console.log("\nTrying DNS lookup...");
    const { exec } = require('child_process');
    exec('nslookup media.wat-factory.cloud', (error, stdout, stderr) => {
        if (stdout) console.log(stdout);
        if (stderr) console.log("Error:", stderr);
    });
}

main();
