const { chromium } = require('playwright');

(async () => {
  const browser = await chromium.launch({
    headless: true,
    args: ['--no-sandbox', '--disable-setuid-sandbox']
  });

  const context = await browser.newContext({
    viewport: { width: 1920, height: 1080 }
  });

  const page = await context.newPage();

  try {
    console.log('Navigating to media.wat-factory.cloud...');
    await page.goto('https://media.wat-factory.cloud', { waitUntil: 'networkidle', timeout: 60000 });

    console.log('Page loaded. Taking screenshot...');
    await page.screenshot({ path: '/job/tmp/01_homepage.png', fullPage: true });

    console.log('Looking for NCA Toolkit or transcription features...');
    const pageContent = await page.content();

    // Look for relevant links or buttons
    const links = await page.$$eval('a', elements => 
      elements.map(el => ({ text: el.textContent.trim(), href: el.href }))
    );

    console.log('Found links:', JSON.stringify(links, null, 2));

    // Look for forms or input fields
    const inputs = await page.$$eval('input', elements =>
      elements.map(el => ({ type: el.type, placeholder: el.placeholder, name: el.name, id: el.id }))
    );

    console.log('Found inputs:', JSON.stringify(inputs, null, 2));

    // Look for buttons
    const buttons = await page.$$eval('button', elements =>
      elements.map(el => ({ text: el.textContent.trim(), type: el.type, id: el.id }))
    );

    console.log('Found buttons:', JSON.stringify(buttons, null, 2));

    // Check page title and headings
    const title = await page.title();
    console.log('Page title:', title);

    const headings = await page.$$eval('h1, h2, h3', elements =>
      elements.map(el => el.textContent.trim())
    );

    console.log('Headings:', JSON.stringify(headings, null, 2));

  } catch (error) {
    console.error('Error:', error.message);
    await page.screenshot({ path: '/job/tmp/error.png', fullPage: true });
  } finally {
    await browser.close();
  }
})();
